import { StreamableCategory } from "./StreamableCategory";

/**
 * Represents a film as a type of streamable content.
 */
export class Film extends StreamableCategory<Film> {
  constructor(name: string, duration: number, year: number, genre: string) {
    super(name, duration, year, genre);
  }

  /**
   * Returns a string representation of the value of the Film object.
   */
  public valueOf(): string {
    return this._name;
  }

  /**
  * Determines whether the Film object is equal to another Film object.
  * @param film - the other Film object to compare to.
  * @returns true if the Film objects are equal, false otherwise.
  */
  public equals(film: Film): boolean {
    return (
      this._name === film._name &&
      this._duration === film._duration &&
      this._year === film._year &&
      this._genre === film._genre
    );
  }

  /**
   * Prints a string representation of the Film object to the console.
   */
  public print() {
    console.log(this.toString());
  }

  /**
   * Returns a string representation of the Film object.
   */
  public toString(): string {
    return `FilmTitle: ${this.name}, Duration: ${this.duration}, Genre: ${this.genre}, Duration: ${this.duration}`;
  }
}

/**
 * Interface representing a category of streamable content.
 * @typeparam T - The type of object that implements this interface.
 */
interface StreamableCategoryInterface<T> {
  name: string;
  duration: number;
  year: number;
  genre: string;
  valueOf(): string;
  equals(other: T): boolean;
}

/**
 * Abstract class representing a category of streamable content.
 * @typeparam T - The type of object that extends this abstract class.
 */
export abstract class StreamableCategory<T> implements StreamableCategoryInterface<T> {
  /**
    * Constructs a new StreamableCategory object.
    * @param _name The name of the streamable content.
    * @param _duration The duration of the streamable content.
    * @param _year The year when the streamable content was released.
    * @param _genre The genre of the streamable content.
   */
  constructor(
    protected _name: string,
    protected _duration: number,
    protected _year: number,
    protected _genre: string,
  ) {}

  /**
   * Returns the name of the streamable content.
   */
  get name(): string { return this._name; }

  /**
   * Returns the duration of the streamable content.
   */
  get duration(): number { return this._duration; }

  /**
   * Returns the year when the streamable content was released.
   */
  get year(): number { return this._year; }

  /**
   * Returns the genre of the streamable content.
   */
  get genre(): string { return this._genre; }

  /**
    * Sets the name of the streamable content.
    * @param name_ The new name of the streamable content.
   */
  set name(name_: string) { this._name = name_; }

  /**
   * Sets the duration of the streamable content.
    * @param duration_ The new duration of the streamable content.
   */
  set duration(duration_: number) { this._duration = duration_; }

  /**
   * Sets the year when the streamable content was released.
    * @param year_ The new year when the streamable content was released.
   */
  set year(year_: number) { this._year = year_; }

  /**
   * Sets the genre of the streamable content.
    * @param genre_ - the new genre of the streamable content.
   */
  set genre(genre_: string) { this._genre = genre_; }

  /**
   * Returns a string representation of the StreamableCategory object.
   */
  public abstract valueOf(): string;  

  /**
    * Determines whether the StreamableCategory object is equal to another object of type T.
    * @param other The other object to compare to.
    * @returns True if the objects are equal, false otherwise.
   */
  public abstract equals(other: T): boolean;

  /**
   * Prints a string representation of the StreamableCategory object to the console.
   */
  public abstract print(): void;

  /**
   * Returns a string representation of the StreamableCategory object.
   */
  public abstract toString(): string;
}

import { Film } from "./Film";
import { BasicStreamableCollection } from "./BasicStreamableCollection";

/**
 * Represents a collection of films extending abstract class BasicStreamableCollection.
 */
export class FilmsCollection extends BasicStreamableCollection<Film> {
  constructor(...films: Film[]) {
    super(...films);
  }

  /**
   * Searches the collection for items that match the provided search term
   * @param searchTerm The search term to match against the items in the collection.
   * @returns An array containing the indexes of the matching items.
   */
  search(searchTerm: Film): number[] {
    let pos_arr: number[] = [];
    for (let i: number = 0; i < this._items.length; i++) {
      if (this._items[i] === searchTerm) {
        pos_arr.push(i);
      }
    }
    return pos_arr;
  }

  /**
   * Searches the collection for items that have a title that matches the provided title.
   * @param title The title to match against the items titles in the collection.
   * @returns An array containing the items that have a title that matches the provided title.
   */
  searchByTitle(title: string): Film[] {
    return this._items.filter((film) =>
      film.name.toLowerCase().includes(title.toLowerCase())
    );
  }

  /**
   * Searches the collection for items that have a duration that matches the provided duration.
   * @param duration The duration to match against the items' durations in the collection.
   * @returns An array containing the items that have a duration that matches the provided duration.
   */
  searchByDuration(duration: number): Film[] {
    return this._items.filter((film) => film.duration === duration);
  }

  /**
   * Searches the collection for items that have a year that matches the provided year.
   * @param year The year to match against the items' years in the collection.
   * @returns An array containing the items that have a year that matches the provided year.
   */
  searchByYear(year: number): Film[] {
    return this._items.filter((film) => film.year === year);
  }

  /**
   * Searches the collection for items that have a genre that matches the provided genre.
   * @param genre The genre to match against the items' genres in the collection.
   * @returns An array containing the items that have a genre that matches the provided genre.
   */
  searchByGenre(genre: string): Film[] {
    return this._items.filter(
      (film) => film.genre.toLowerCase() === genre.toLowerCase()
    );
  }

  /**
   * Sorts the items in the collection by title.
   */
  sortByTitle(): void {
    this._items.sort((a, b) => a.name.localeCompare(b.name));
  }

  /**
   * Sorts the items in the collection by duration.
   */
  sortByDuration(): void {
    this._items.sort((a, b) => a.duration - b.duration);
  }

  /**
   * Sorts the items in the collection by year.
   */
  sortByYear(): void {
    this._items.sort((a, b) => a.year - b.year);
  }

  /**
   * Sorts the items in the collection by genre.
   */
  sortByGenre(): void {
    this._items.sort((a, b) => a.genre.localeCompare(b.genre));
  }
}

/**
 * Interface that defines common methods for collections
 */
interface CollectionInterface<T> {
  size(): number;
  push(...newItems: T[]): void;
  pop(): T | undefined;
  unshift(...items: T[]): void;
  shift(): T | undefined;
  slice(start?: number, end?: number): T[];
  splice(start: number, deleteCount: number, ...items: T[]): T[];
  at(indx: number): T;
  [indx: number]: T;
  search(searchTerm: T): number[];
  getItems(): T[];
}

/**
 * Interface that extends 'CollectionInterface' adding some specifics methods for searching and sorting streamable elements of a streamable collection.
 */
interface Streamable<T> extends CollectionInterface<T> {
  searchByTitle(title: string): T[];
  searchByDuration(duration: number): T[];
  searchByYear(year: number): T[];
  searchByGenre(genre: string): T[];
  sortByTitle(): void;
  sortByDuration(): void;
  sortByYear(): void;
  sortByGenre(): void;
}

/**
 * This abstract class represents a basic streamable collection that can be extended to create different collections of items.
 * @template T Represents the type of the items contained in the collection.
 */
export abstract class BasicStreamableCollection<T> implements Streamable<T> {
  protected _items: T[];

  /**
   * Creates an instace of the BasicStreamableCollection class.
   * @param items The initial items to be added to the collection.
   */
  constructor(...items: T[]) { this._items = items; }

  /**
   * Returns the number of items in the collection.
   * @returns The number of items in the collection.
   */
  public size(): number { return this._items.length; }

  /**
   * Adds one or more new items to the end of the collection.
   * @param newItems The new items to be added into the collection.
   */
  public push(...newItems: T[]) { this._items.push(...newItems); }

  /**
   * Removes and returns the las item in the collection.
   * @returns The last item in the collection or undefined if the collection is empty.
   */
  public pop(): T | undefined { return this._items.pop(); }

  /**
   * Adds one or more new items to the beginning of the collection.
   * @param items The new items to be added in the collection.
   */
  public unshift(...items: T[]) { this._items.unshift(...items); }

  /**
   * Removes and returns the first item in the colecction.
   * @returns The first item in the collection or undefined if the collection is empty.
   */
  public shift(): T | undefined { return this._items.shift(); }

  /**
   * Returns a copy of a portion of the collection.
   * @param start The initial index.
   * @param end The last index.
   * @returns A new array containing the extracted elements.
   */
  public slice(start?: number, end?: number): T[] {
    return this._items.slice(start, end);
  }

  /**
   * Removes and/or adds items to the collection, starting at the specified index.
   * @param start The initial index.
   * @param deleteCount The number of items to be removed.
   * @param items The items to be added to the collection.
   * @returns A collection containing the removed elements.
   */
  public splice(start: number, deleteCount: number = 0, ...items: T[]): T[]  {
    return this._items.splice(start, deleteCount, ...items);
  }

  /**
   * Gets or sets the item at the specified index in the collection.
   * @param indx The index of the item.
   * @returns The item at the specified idnex.
   */
  public at(indx: number): T {
    return this._items[indx];
  }

  /**
   * Gets or sets the item at the specified index of the collection.
   * @param indx The index of the item.
   * @returns The item at the specified index.
   */
  [indx: number]: T;

  /**
   * Gets the items in the collection.
   * @returns An array of items in the collection.
   */
  public getItems(): T[] { return this._items; }

  /**
   * An abstract method that searches the collection for items that match the provided search term
   * @param searchTerm The search term to match against the items in the collection.
   * @returns An array containing the indexes of the matching items.
   */
  public abstract search(searchTerm: T): number[];

  /**
   * An abstract method that searches the collection for items that have a title that matches the provided title.
   * @param title The title to match against the items' titles in the collection.
   * @returns An array containing the items that have a title that matches the provided title.
   */
  public abstract searchByTitle(title: string): T[];

  /**
   * An abstract method that searches the collection for items that have a duration that matches the provided duration.
   * @param duration The duration to match against the items' durations in the collection.
   * @returns An array containing the items that have a duration that matches the provided duration.
   */
  public abstract searchByDuration(duration: number): T[];

  /**
   * An abstract method that searches the collection for items that have a year that matches the provided year.
   * @param year The year to match against the items' years in the collection.
   * @returns An array containing the items that have a year that matches the provided year.
   */
  public abstract searchByYear(year: number): T[];

  /**
   * An abstract method that searches the collection for items that have a genre that matches the provided genre.
   * @param genre The genre to match against the items' genres in the collection.
   * @returns An array containing the items that have a genre that matches the provided genre.
   */
  public abstract searchByGenre(genre: string): T[];

  /**
   * An abstract method that sorts the items in the collection by title.
   */
  public abstract sortByTitle(): void;

  /**
   * An abstract method that sorts the items in the collection by duration.
   */
  public abstract sortByDuration(): void;

  /**
   * An abstract method that sorts the items in the collection by year.
   */
  public abstract sortByYear(): void;

  /**
   * An abstract method that sorts the items in the collection by genre.
   */
  public abstract sortByGenre(): void;
}

import {SearchableCollection} from "./SearchableCollection"

/**
 * Class that represents a NumberCollection
 * It inhereits from abstract class SearchableCollection
 *   Methods: 
 *     search: It returns a number array with position of occurrencies
 */
export class NumbericSearchableCollection extends SearchableCollection<number> {
  constructor(num_arr: number[]) {
    super();
    this._items = num_arr;
  }

  /**
   * It returns a number array with position of occurrencies
   * @param num Number that is going to be searched
   * @returns Array with occurrencies position
   */
  search(num: number): number[] {
    let result_pos: number[] = [];
    for (let i = 0; i < this._items.length; i++) {
      if (this._items[i] === num) { result_pos.push(i); }
    } 
    return result_pos;
  }
}

